mapper.delete:

delete from screening
where cinema_id = #{cinemaId,jdbcType=INTEGER}
and movie_id = #{movieId,jdbcType=INTEGER}
and show_date = #{showDate,jdbcType=DATE}
and start_time = #{startTime,jdbcType=TIME}
and hall = #{hall,jdbcType=VARCHAR}
and source_id = #{sourceId,jdbcType=INTEGER}

